const parsedItems = items.map((item) => {
  const parsedData = JSON.parse(item?.json?.data);
  return { json: parsedData };
});
return parsedItems;

// 1) Ð—Ð±Ð¸Ñ€Ð°Ñ”Ð¼Ð¾ Ð²ÑÑ– Ð²Ñ…Ñ–Ð´Ð½Ñ– items
const items = $input.all();

// 2) ÐŸÐ°Ñ€ÑÐ¸Ð¼Ð¾ requisition Ñ– Ð²Ñ–Ð´Ð´Ð°Ñ”Ð¼Ð¾ Ñ‚Ñ–Ð»ÑŒÐºÐ¸ department URL + ÑÐ°Ð¼ Ð¾Ð±'Ñ”ÐºÑ‚ requisition
const out = [];

for (const item of items) {
  let envelope = item?.json?.data;

  // data Ð¼Ð¾Ð¶Ðµ Ð±ÑƒÑ‚Ð¸ Ñ€ÑÐ´ÐºÐ¾Ð¼ Ð°Ð±Ð¾ Ð²Ð¶Ðµ Ð¾Ð±'Ñ”ÐºÑ‚Ð¾Ð¼
  if (typeof envelope === 'string') {
    try {
      envelope = JSON.parse(envelope);          // -> { data: {...} }
    } catch (e) {
      throw new Error(`Invalid requisition JSON: ${e.message}`);
    }
  }

  const requisition = envelope?.data ?? envelope;
  if (!requisition || typeof requisition !== 'object') continue;

  const deptUrl = requisition?.relationships?.department?.links?.related;
  if (!deptUrl) continue; // ÑÐºÑ‰Ð¾ Ð½ÐµÐ¼Ð°Ñ” URL â€” Ð½Ñ–Ñ‡Ð¾Ð³Ð¾ Ð½Ðµ Ð²Ñ–Ð´Ð´Ð°Ñ”Ð¼Ð¾

  out.push({
    json: {
      key: 'department',
      url: deptUrl,
      jobId: requisition.id ?? null,
      requisitionData: requisition, // Ð¿ÐµÑ€ÐµÐ´Ð°Ñ”Ð¼Ð¾ Ð´Ð°Ð»Ñ– Ð¿Ð¾ Ð¿Ð¾Ñ‚Ð¾ÐºÑƒ
    },
  });
}

return out;

/**
 * Build final employee payload.
 * hireDate: candidate.updated_at -> webhook.updated_at -> today
 */

// helpers
const toYmd = (d) => {
  const y = d.getFullYear();
  const m = String(d.getMonth() + 1).padStart(2, '0');
  const day = String(d.getDate()).padStart(2, '0');
  return `${y}-${m}-${day}`;
};

// sources
const deptItems = $input.all(); // parsed department responses from "convert to json object"
const webhook   = $('Webhook').first().json.body;

// requisition: read directly from HTTP node (stringified in .data)
let reqEnv = null;
try {
  const raw = $('HTTP Request to TeamTailor Show the Job Requisition').first().json.data;
  if (typeof raw === 'string') reqEnv = JSON.parse(raw);
  else if (raw && typeof raw === 'object') reqEnv = raw;
} catch (e) {
  console.warn('Requisition parse failed:', e.message);
}
const requisition = reqEnv?.data ?? null;
const reqAttrs   = requisition?.attributes ?? {};
const cfa        = reqAttrs['custom-form-answers'] ?? {};

// department name from input items
let departmentName = null;
for (const it of deptItems) {
  const j = it.json;
  if (j?.data?.type === 'departments' && j.data?.attributes?.name) {
    departmentName = j.data.attributes.name; break;
  }
  if (j?.type === 'departments' && j?.attributes?.name) {
    departmentName = j.attributes.name; break;
  }
}

// team/division & level
const teamName       = cfa.team ?? null;                    // "AMZ [Production]"
const candidateLevel = cfa.level_of_candidate ?? null;      // "Middle"
const reqTitle       = reqAttrs['job-title'] ?? null;

// job title with level prefix
let jobTitle = webhook.job_title || reqTitle || null;
if (candidateLevel && jobTitle) jobTitle = `${candidateLevel} ${jobTitle}`.trim();

// hireDate: candidate.updated_at -> webhook.updated_at -> today
let hireDate = null;
const updCandidate = webhook?.candidate?.updated_at;
const updWebhook   = webhook?.updated_at;

if (updCandidate)      hireDate = toYmd(new Date(updCandidate));
else if (updWebhook)   hireDate = toYmd(new Date(updWebhook));
else                   hireDate = toYmd(new Date());

// candidate basics
const cand = webhook.candidate || {};
const employee = {
  firstName: cand.first_name || null,
  lastName : cand.last_name  || null,
  jobTitle,
  hireDate,
  email    : cand.email || null,
};

if (departmentName) employee.department = departmentName;
if (teamName)       employee.division   = teamName;
if (cand.phone)     employee.phone      = cand.phone;

return [{ json: employee }];

// n8n Code Node: Process Field Discovery (Alternative for Split Items)

/***********************************************************************
 * 1. GET ALL ITEMS (when Split Into Items is ON)
 ***********************************************************************/
// When "Split Into Items" is ON, we need to collect all items
const allItems = $input.all();
const metaFields = allItems.map(item => item.json);

// The test employee data from the starting node.
const employeeData = $("Build Employee Payload").all();

/***********************************************************************
 * 2. VALIDATE THE INPUT DATA STRUCTURE
 ***********************************************************************/
console.log(`Received ${metaFields.length} meta fields from BambooHR`);

/***********************************************************************
 * 3. MAP FIELD NAMES TO IDs
 ***********************************************************************/
const fieldMapping = {};

for (const field of metaFields) {
  // We only care about fields that can be managed via the API.
  if (field.manageable !== 'yes') {
    continue;
  }

  const id = parseInt(field.fieldId, 10);
  const alias = (field.alias || '').toLowerCase();
  const name = (field.name || '').toLowerCase();

  // Use the 'alias' for primary matching when available
  if (alias) {
    switch (alias) {
      case 'department':
        fieldMapping.department = id;
        break;
      case 'division':
        fieldMapping.division = id;
        break;
      case 'jobtitle':
        fieldMapping.jobTitle = id;
        break;
      case 'location':
        fieldMapping.location = id;
        break;
      case 'team':
        fieldMapping.team = id;
        break;
      case 'customteams':
        // Fallback for custom teams field
        if (!fieldMapping.team) {
          fieldMapping.team = id;
        }
        break;
    }
  }

  // Secondary check based on name if alias didn't match
  if (!alias || !fieldMapping.jobTitle) {
    if (name === 'job title' && !fieldMapping.jobTitle) {
      fieldMapping.jobTitle = id;
    }
  }
}

/***********************************************************************
 * 4. DEBUG OUTPUT
 ***********************************************************************/
console.log('Discovered field mappings:', JSON.stringify(fieldMapping, null, 2));

// Log which fields we found and which we didn't
const expectedFields = ['department', 'division', 'jobTitle'];
for (const fieldName of expectedFields) {
  if (fieldMapping[fieldName]) {
    console.log(`âœ“ Found ${fieldName}: fieldId = ${fieldMapping[fieldName]}`);
  } else {
    console.warn(`âœ— Could not find ${fieldName} field`);
  }
}

/***********************************************************************
 * 5. FINAL OUTPUT
 ***********************************************************************/
return [{
  json: {
    fieldMapping,
    employeeData,
    metaFields
  }
}];

const { fieldMapping, employeeData, metaFields } = $input.all()[0].json;

// Function to find existing options for a field
function getExistingOptions(fieldName) {
  const fieldId = fieldMapping[fieldName];
  if (!fieldId) return [];

  const field = metaFields.find(f => f.fieldId === fieldId);
  return field ? field.options || [] : [];
}

// Function to check if option exists
function optionExists(fieldName, optionValue) {
  const options = getExistingOptions(fieldName);
  return options.find(option => 
    (option.value || '').toLowerCase() === optionValue.toLowerCase() ||
    (option.name || '').toLowerCase() === optionValue.toLowerCase()
  );
}

// Check required organizational fields
const requiredFields = ['department', 'division', 'jobTitle'];
const validationResults = {};
const missingFields = [];

for (const fieldName of requiredFields) {
  const fieldId = fieldMapping[fieldName];
  const optionValue = employeeData[fieldName];

  if (!fieldId) {
    validationResults[fieldName] = { 
      status: 'field_missing', 
      message: `Field ID not found for ${fieldName}` 
    };
  } else if (!optionValue) {
    validationResults[fieldName] = { 
      status: 'value_missing', 
      message: `No value provided for ${fieldName}` 
    };
  } else {
    const existingOption = optionExists(fieldName, optionValue);
    if (existingOption) {
      validationResults[fieldName] = {
        status: 'exists',
        option: existingOption,
        fieldId: fieldId
      };
    } else {
      validationResults[fieldName] = {
        status: 'needs_creation',
        value: optionValue,
        fieldId: fieldId
      };
      missingFields.push(fieldName);
    }
  }
}

console.log('Validation results:', validationResults);
console.log('Missing fields:', missingFields);

return [{
  json: {
    validationResults: validationResults,
    missingFields: missingFields,
    fieldMapping: fieldMapping,
    employeeData: employeeData,
    metaFields: metaFields,
    needsCreation: missingFields.length > 0
  }
}];

const { missingFields, validationResults, metaFields, fieldMapping, employeeData } = $input.all()[0].json;

// Create meta field options for missing fields
const creationPromises = [];

for (const fieldName of missingFields) {
  const fieldId = validationResults[fieldName].fieldId;
  const newValue = validationResults[fieldName].value;

  // Get existing options for the field
  const field = metaFields.find(f => f.fieldId === fieldId);
  const existingOptions = field ? field.options || [] : [];

  // Add new option to existing options - using the correct format that matches Python
  const updatedOptions = [...existingOptions, { "value": newValue }];

  creationPromises.push({
    fieldName: fieldName,
    fieldId: fieldId,
    newValue: newValue,
    updatedOptions: updatedOptions
  });
}

return creationPromises.map(item => ({ json: { ...item, employeeData, fieldMapping } }));

const { json: root } = $('Validate Meta Field Options').first();

// Unwrap employee data
const employeeDataArr = root.employeeData;
const employeeData = Array.isArray(employeeDataArr)
  ? (employeeDataArr[0]?.json || {})
  : employeeDataArr;

// Field mapping / meta-field options
const fieldMapping = root.fieldMapping;
const allItems      = $input.all();
const updatedMetaFields = (allItems.length === 1 && Array.isArray(allItems[0].json))
  ? allItems[0].json           // full array came in a single item
  : allItems.map(i => i.json); // split items

function getOptionId(fieldName, optionValue) {
  const fieldId = fieldMapping[fieldName];
  if (!fieldId) return null;

  const field = updatedMetaFields.find(f => f.fieldId === fieldId);
  if (!field || !field.options) return null;

  const opt = field.options.find(o =>
    (o.value || '').toLowerCase() === optionValue.toLowerCase() ||
    (o.name  || '').toLowerCase() === optionValue.toLowerCase()
  );
  return opt?.id ?? null;
}

// Build payload
const employeePayload = {
  firstName: employeeData.firstName,
  lastName:  employeeData.lastName,
  workEmail: employeeData.email,
  hireDate:  employeeData.hireDate,
  employmentHistoryStatus: employeeData.employmentStatus || 'Full-Time',
  payRate:  employeeData.salary,
  payType:  'Salary',
  payPer:   'Year',
  exempt:   employeeData.exempt   || 'Exempt',
  location: employeeData.location || 'Remote',
};

// Hybrid organisational fields
['department', 'division', 'jobTitle'].forEach(field => {
  if (employeeData[field]) {
    employeePayload[field] = employeeData[field];
    const id = getOptionId(field, employeeData[field]);
    if (id) employeePayload[`${field}Id`] = id;
  }
});

console.log('Employee payload:', employeePayload);

return [
  {
    json: {
      employeePayload,
      originalData: employeeData   // already unwrapped, easier to inspect
    }
  }
];

const response = $input.all()[0];
const employeeData = $('Prepare Employee Data').first().json.originalData;
const companyDomain = $('Set Company Domain').first().json.companyDomain;

// Extract employee ID from Location header if available
let employeeId = null;
if (response.headers && response.headers.location) {
  const locationMatch = response.headers.location.match(/\/employees\/(\d+)/);
  if (locationMatch) {
    employeeId = parseInt(locationMatch[1]);
  }
}

return [{
  json: {
    success: true,
    message: "Employee created successfully",
    employee: {
      id: employeeId,
      name: `${employeeData.firstName} ${employeeData.lastName}`,
      email: employeeData.email,
      department: employeeData.department,
      division: employeeData.division,
      jobTitle: employeeData.jobTitle
    },
    bambooHrUrl: employeeId ? 
      `https://${companyDomain}.bamboohr.com/employees/${employeeId}` : null,
    createdAt: new Date().toISOString()
  }
}];

