return out;

// Fetch all job offers with pagination handling
const apiToken = "YOUR_TEAMTAILOR_API_TOKEN"; // From config.env
const baseUrl = 'https://api.teamtailor.com/v1/job-offers';
const pageSize = 30; // Maximum allowed by TeamTailor API

let allOffers = [];
let nextUrl = `${baseUrl}?include=job-application,user&page[size]=${pageSize}`;
let pageCount = 0;
const maxPages = 50; // Safety limit to prevent infinite loops

console.log('Starting to fetch job offers with pagination...');

while (nextUrl && pageCount < maxPages) {
  try {
    // Make HTTP request with proper headers
    const response = await this.helpers.httpRequest({
      method: 'GET',
      url: nextUrl,
      headers: {
        'Authorization': `Bearer ${apiToken}`,
        'X-Api-Version': '20240404',
        'Accept': 'application/vnd.api+json',
        'Content-Type': 'application/vnd.api+json'
      },
      json: true
    });

    pageCount++;
    console.log(`Fetched page ${pageCount}, got ${response.data?.length || 0} offers`);

    // Add offers from this page
    if (response.data && Array.isArray(response.data)) {
      allOffers = allOffers.concat(response.data);
    }

    // Check for next page
    nextUrl = response.links?.next || null;

    // Log pagination info
    if (response.meta) {
      console.log(`Total records: ${response.meta['record-count']}, Total pages: ${response.meta['page-count']}`);
    }

  } catch (error) {
    console.error(`Error fetching page ${pageCount + 1}:`, error.message);
    console.error('Full error details:', error);
    throw error;
  }
}

console.log(`Pagination complete. Fetched ${pageCount} pages with total ${allOffers.length} offers`);

// Return all offers as a single item with data array
return [{
  json: {
    data: allOffers,
    meta: {
      totalOffers: allOffers.length,
      pagesProcessed: pageCount
    }
  }
}];

// Find offer for this application
const webhook = $('Webhook').first().json.body;
const applicationId = String(webhook.id); // Ensure it's a string
const offersResponse = $input.all()[0].json;

console.log('Looking for application ID:', applicationId);
console.log('Total offers received:', offersResponse.data?.length || 0);

let matchingOffer = null;
let debugInfo = [];

// Search in data array (already parsed from paginated fetch)
if (offersResponse && offersResponse.data && Array.isArray(offersResponse.data)) {
  console.log('Searching through offers...');

  for (const offer of offersResponse.data) {
    const jobAppData = offer.relationships?.['job-application']?.data;
    if (jobAppData) {
      const offerId = String(jobAppData.id);
      debugInfo.push({
        offerId: offer.id,
        jobApplicationId: offerId,
        matches: offerId === applicationId
      });

      if (offerId === applicationId) {
        matchingOffer = offer;
        console.log('Found matching offer:', offer.id);
        break;
      }
    }
  }
} else {
  console.log('No offers data array found');
}

console.log('Debug info (first 10):', JSON.stringify(debugInfo.slice(0, 10), null, 2));

// If we found a matching offer, extract key data
if (matchingOffer) {
  const offerDetails = matchingOffer.attributes.details || {};

  return [{
    json: {
      found: true,
      offerId: matchingOffer.id,
      status: matchingOffer.attributes.status,
      details: offerDetails,
      salary: offerDetails.salary || offerDetails.Compensation,
      startDate: offerDetails['Start day'] || offerDetails['start-date'],
      applicationId: applicationId,
      offerData: matchingOffer,
      debugInfo: debugInfo.slice(0, 10), // Limit debug info size
      totalOffersSearched: offersResponse.data.length,
      pagesProcessed: offersResponse.meta?.pagesProcessed || 1
    }
  }];
} else {
  // No offer found - use default values
  return [{
    json: {
      found: false,
      applicationId: applicationId,
      salary: null,
      startDate: null,
      message: "No offer found for this application",
      debugInfo: debugInfo.slice(0, 10),
      totalOffersSearched: offersResponse.data?.length || 0,
      pagesProcessed: offersResponse.meta?.pagesProcessed || 0
    }
  }];
}

/**
 * Build final employee payload using MERGED data
 * IMPROVED VERSION with comprehensive edge case handling
 */

// Helper functions
const toYmd = (d) => {
  const y = d.getFullYear();
  const m = String(d.getMonth() + 1).padStart(2, '0');
  const day = String(d.getDate()).padStart(2, '0');
  return `${y}-${m}-${day}`;
};

const safeDateParse = (dateStr) => {
  if (!dateStr) return null;
  try {
    const date = new Date(dateStr);
    return isNaN(date.getTime()) ? null : date;
  } catch (error) {
    console.warn('Failed to parse date:', dateStr, error.message);
    return null;
  }
};

const parseSalaryInfo = (salaryStr) => {
  if (!salaryStr) return { amount: null, payPer: 'Year' };

  if (typeof salaryStr === 'number') {
    return { amount: salaryStr, payPer: 'Year' };
  }

  const salaryString = salaryStr.toString();
  const amountMatch = salaryString.match(/[0-9,]+/);
  const amount = amountMatch ? parseInt(amountMatch[0].replace(/,/g, '')) : null;

  let payPer = 'Year';
  if (salaryString.toLowerCase().includes('month')) {
    payPer = 'Month';
  } else if (salaryString.toLowerCase().includes('week')) {
    payPer = 'Week';
  } else if (salaryString.toLowerCase().includes('hour')) {
    payPer = 'Hour';
  } else if (salaryString.toLowerCase().includes('day')) {
    payPer = 'Day';
  }

  return { amount, payPer };
};

// ===== MAIN LOGIC =====

// 1. VALIDATE WEBHOOK DATA (CRITICAL)
const webhook = $('Webhook').first().json.body;

if (!webhook || !webhook.candidate) {
  console.error('Invalid webhook structure:', webhook);
  throw new Error('Webhook missing required candidate data');
}

const cand = webhook.candidate || {};
if (!cand.first_name || !cand.last_name || !cand.email) {
  console.error('Missing required candidate fields:', {
    firstName: !!cand.first_name,
    lastName: !!cand.last_name,
    email: !!cand.email
  });
  throw new Error('Candidate missing required fields (first_name, last_name, email)');
}

// 2. PROCESS MERGED DATA
const mergedItems = $input.all();

// Validate merge data
if (mergedItems.length < 2) {
  console.warn('Merge node received insufficient data:', mergedItems.length, 'items');
}

let offerData = null;
let departmentData = null;
let requisitionData = null;

// Process merged items
for (const item of mergedItems) {
  const json = item.json;

  // Check if this is offer data
  if (json.found !== undefined && json.applicationId) {
    offerData = json;
  }

  // Check if this is department data
  if (json.type === 'departments' || (json.data && json.data.type === 'departments')) {
    departmentData = json.data || json;
  }

  // Check if requisition data is embedded
  if (json.requisitionData) {
    requisitionData = json.requisitionData;
  }
}

// 3. EXTRACT REQUISITION DATA WITH FALLBACKS
let reqAttrs = {};
let cfa = {};
let reqTitle = null;
let teamName = null;
let candidateLevel = null;

if (requisitionData?.attributes) {
  reqAttrs = requisitionData.attributes;
  cfa = reqAttrs['custom-form-answers'] || {};
  reqTitle = reqAttrs['job-title'];
  teamName = cfa.team;
  candidateLevel = cfa.level_of_candidate;
} else {
  console.warn('No requisition data found, using webhook fallbacks');
  // Fallback to webhook data
  reqTitle = webhook.job_title;
  teamName = webhook.team || null;
  candidateLevel = webhook.level || null;
}

// 4. EXTRACT ALL NEEDED DATA
const departmentName = departmentData?.attributes?.name || null;

// Build job title with level
let jobTitle = webhook.job_title || reqTitle || null;
if (candidateLevel && jobTitle) {
  jobTitle = `${candidateLevel} ${jobTitle}`.trim();
}

// 5. DETERMINE HIRE DATE WITH SAFE PARSING
let hireDate = null;
if (offerData?.found && offerData.startDate) {
  const parsedDate = safeDateParse(offerData.startDate);
  hireDate = parsedDate ? toYmd(parsedDate) : null;
}

if (!hireDate) {
  const updCandidate = safeDateParse(webhook?.candidate?.updated_at);
  const updWebhook = safeDateParse(webhook?.updated_at);

  if (updCandidate) hireDate = toYmd(updCandidate);
  else if (updWebhook) hireDate = toYmd(updWebhook);
  else hireDate = toYmd(new Date()); // Final fallback to today
}

// 6. EXTRACT SALARY INFO
let salaryInfo = { amount: null, payPer: 'Year' };
if (offerData?.found && offerData.salary) {
  salaryInfo = parseSalaryInfo(offerData.salary);
}

// 7. VALIDATE MINIMUM REQUIRED DATA
console.log('Data validation:', {
  hasOfferData: !!offerData,
  hasDepartmentData: !!departmentData,
  hasRequisitionData: !!requisitionData,
  mergedItemsCount: mergedItems.length,
  offerFound: offerData?.found || false,
  departmentName: departmentName || 'NOT_FOUND',
  jobTitle: jobTitle || 'NOT_FOUND',
  hireDate: hireDate || 'NOT_FOUND'
});

if (!offerData && !requisitionData) {
  console.error('Critical: No offer or requisition data available');
  throw new Error('Insufficient data to create employee');
}

// 8. BUILD EMPLOYEE OBJECT
const employee = {
  firstName: cand.first_name,
  lastName: cand.last_name,
  jobTitle: jobTitle || 'Unknown Position',
  hireDate,
  email: cand.email,
  salary: salaryInfo.amount,
  payPer: salaryInfo.payPer,
  employmentStatus: (offerData?.found && offerData?.status === 'accepted') ? 'Active' : 'Pending',
  offerStatus: offerData?.status || 'no_offer',
  offerId: offerData?.offerId || null
};

// Add optional fields
if (departmentName) employee.department = departmentName;
if (teamName) employee.division = teamName;
if (cand.phone) employee.phone = cand.phone;

// 9. FINAL VALIDATION AND LOGGING
console.log('Employee Payload Created:', {
  name: `${employee.firstName} ${employee.lastName}`,
  email: employee.email,
  department: employee.department || 'NOT_SET',
  jobTitle: employee.jobTitle,
  hireDate: employee.hireDate,
  salary: employee.salary ? `$${employee.salary} ${employee.payPer}` : 'NOT_SET',
  offerStatus: employee.offerStatus
});

return [{ json: employee }];

// n8n Code Node: Process Field Discovery (Alternative for Split Items)

/***********************************************************************
 * 1. GET ALL ITEMS (when Split Into Items is ON)
 ***********************************************************************/
// When "Split Into Items" is ON, we need to collect all items
const allItems = $input.all();
const metaFields = allItems.map(item => item.json);

// The test employee data from the starting node.
const employeeData = $("Build Employee Payload").all();

/***********************************************************************
 * 2. VALIDATE THE INPUT DATA STRUCTURE
 ***********************************************************************/
console.log(`Received ${metaFields.length} meta fields from BambooHR`);

/***********************************************************************
 * 3. MAP FIELD NAMES TO IDs
 ***********************************************************************/
const fieldMapping = {};

for (const field of metaFields) {
  // We only care about fields that can be managed via the API.
  if (field.manageable !== 'yes') {
    continue;
  }

  const id = parseInt(field.fieldId, 10);
  const alias = (field.alias || '').toLowerCase();
  const name = (field.name || '').toLowerCase();

  // Use the 'alias' for primary matching when available
  if (alias) {
    switch (alias) {
      case 'department':
        fieldMapping.department = id;
        break;
      case 'division':
        fieldMapping.division = id;
        break;
      case 'jobtitle':
        fieldMapping.jobTitle = id;
        break;
      case 'location':
        fieldMapping.location = id;
        break;
      case 'team':
        fieldMapping.team = id;
        break;
      case 'customteams':
        // Fallback for custom teams field
        if (!fieldMapping.team) {
          fieldMapping.team = id;
        }
        break;
    }
  }

  // Secondary check based on name if alias didn't match
  if (!alias || !fieldMapping.jobTitle) {
    if (name === 'job title' && !fieldMapping.jobTitle) {
      fieldMapping.jobTitle = id;
    }
  }
}

/***********************************************************************
 * 4. DEBUG OUTPUT
 ***********************************************************************/
console.log('Discovered field mappings:', JSON.stringify(fieldMapping, null, 2));

// Log which fields we found and which we didn't
const expectedFields = ['department', 'division', 'jobTitle'];
for (const fieldName of expectedFields) {
  if (fieldMapping[fieldName]) {
    console.log(`‚úì Found ${fieldName}: fieldId = ${fieldMapping[fieldName]}`);
  } else {
    console.warn(`‚úó Could not find ${fieldName} field`);
  }
}

/***********************************************************************
 * 5. FINAL OUTPUT
 ***********************************************************************/
return [{
  json: {
    fieldMapping,
    employeeData,
    metaFields
  }
}];

const { fieldMapping, employeeData, metaFields } = $input.all()[0].json;

// Function to find existing options for a field
function getExistingOptions(fieldName) {
  const fieldId = fieldMapping[fieldName];
  if (!fieldId) return [];

  const field = metaFields.find(f => f.fieldId === fieldId);
  return field ? field.options || [] : [];
}

// Function to check if option exists
function optionExists(fieldName, optionValue) {
  const options = getExistingOptions(fieldName);
  return options.find(option => 
    (option.value || '').toLowerCase() === optionValue.toLowerCase() ||
    (option.name || '').toLowerCase() === optionValue.toLowerCase()
  );
}

// Check required organizational fields
const requiredFields = ['department', 'division', 'jobTitle'];
const validationResults = {};
const missingFields = [];

for (const fieldName of requiredFields) {
  const fieldId = fieldMapping[fieldName];
  const optionValue = employeeData[fieldName];

  if (!fieldId) {
    validationResults[fieldName] = { 
      status: 'field_missing', 
      message: `Field ID not found for ${fieldName}` 
    };
  } else if (!optionValue) {
    validationResults[fieldName] = { 
      status: 'value_missing', 
      message: `No value provided for ${fieldName}` 
    };
  } else {
    const existingOption = optionExists(fieldName, optionValue);
    if (existingOption) {
      validationResults[fieldName] = {
        status: 'exists',
        option: existingOption,
        fieldId: fieldId
      };
    } else {
      validationResults[fieldName] = {
        status: 'needs_creation',
        value: optionValue,
        fieldId: fieldId
      };
      missingFields.push(fieldName);
    }
  }
}

console.log('Validation results:', validationResults);
console.log('Missing fields:', missingFields);

return [{
  json: {
    validationResults: validationResults,
    missingFields: missingFields,
    fieldMapping: fieldMapping,
    employeeData: employeeData,
    metaFields: metaFields,
    needsCreation: missingFields.length > 0
  }
}];

const { missingFields, validationResults, metaFields, fieldMapping, employeeData } = $input.all()[0].json;

// Create meta field options for missing fields
const creationPromises = [];

for (const fieldName of missingFields) {
  const fieldId = validationResults[fieldName].fieldId;
  const newValue = validationResults[fieldName].value;

  // Get existing options for the field
  const field = metaFields.find(f => f.fieldId === fieldId);
  const existingOptions = field ? field.options || [] : [];

  // Add new option to existing options - using the correct format that matches Python
  const updatedOptions = [...existingOptions, { "value": newValue }];

  creationPromises.push({
    fieldName: fieldName,
    fieldId: fieldId,
    newValue: newValue,
    updatedOptions: updatedOptions
  });
}

return creationPromises.map(item => ({ json: { ...item, employeeData, fieldMapping } }));

const { json: root } = $('Validate Meta Field Options').first();

// Unwrap employee data
const employeeDataArr = root.employeeData;
const employeeData = Array.isArray(employeeDataArr)
  ? (employeeDataArr[0]?.json || {})
  : employeeDataArr;

// Field mapping / meta-field options
const fieldMapping = root.fieldMapping;
const allItems      = $input.all();
const updatedMetaFields = (allItems.length === 1 && Array.isArray(allItems[0].json))
  ? allItems[0].json           // full array came in a single item
  : allItems.map(i => i.json); // split items

function getOptionId(fieldName, optionValue) {
  const fieldId = fieldMapping[fieldName];
  if (!fieldId) return null;

  const field = updatedMetaFields.find(f => f.fieldId === fieldId);
  if (!field || !field.options) return null;

  const opt = field.options.find(o =>
    (o.value || '').toLowerCase() === optionValue.toLowerCase() ||
    (o.name  || '').toLowerCase() === optionValue.toLowerCase()
  );
  return opt?.id ?? null;
}

// Build enhanced payload with offer data
const employeePayload = {
  firstName: employeeData.firstName,
  lastName:  employeeData.lastName,
  workEmail: employeeData.email,
  hireDate:  employeeData.hireDate,
  employmentHistoryStatus: employeeData.employmentStatus || 'Full-Time',
  payType:  'Salary',
  payPer:   employeeData.payPer || 'Year', // Use dynamic pay period from offer
  exempt:   employeeData.exempt   || 'Exempt',
  location: employeeData.location || 'Remote',
};

// Add salary if available from offer
if (employeeData.salary) {
  employeePayload.payRate = employeeData.salary;
}

// Add phone if available
if (employeeData.phone) {
  employeePayload.mobilePhone = employeeData.phone;
}

// Hybrid organisational fields
['department', 'division', 'jobTitle'].forEach(field => {
  if (employeeData[field]) {
    employeePayload[field] = employeeData[field];
    const id = getOptionId(field, employeeData[field]);
    if (id) employeePayload[`${field}Id`] = id;
  }
});

// Add custom fields for offer tracking
if (employeeData.offerId) {
  employeePayload.customOfferId = employeeData.offerId;
}
if (employeeData.offerStatus) {
  employeePayload.customOfferStatus = employeeData.offerStatus;
}

console.log('Enhanced employee payload:', employeePayload);

return [
  {
    json: {
      employeePayload,
      originalData: employeeData,
      offerData: {
        offerId: employeeData.offerId,
        offerStatus: employeeData.offerStatus,
        salary: employeeData.salary,
        hireDate: employeeData.hireDate
      }
    }
  }
];

// Check if employee already exists by email
const employeeData = $('Prepare Employee Data').first().json;
const datasetResponse = $input.all()[0].json;
const targetEmail = employeeData.originalData.email;

console.log('Checking if employee exists with email:', targetEmail);
console.log('Dataset response:', datasetResponse);

let existingEmployee = null;
let employeeExists = false;

// Check if we have a valid dataset response
if (datasetResponse && datasetResponse.data && Array.isArray(datasetResponse.data)) {
  // Search through dataset for matching email
  for (const employee of datasetResponse.data) {
    if (employee.email && employee.email.toLowerCase() === targetEmail.toLowerCase()) {
      existingEmployee = employee;
      employeeExists = true;
      console.log('Found existing employee:', employee);
      break;
    }
  }
}

if (employeeExists) {
  console.log('Employee already exists, skipping creation');
  return [{
    json: {
      employeeExists: true,
      existingEmployee: existingEmployee,
      message: 'Employee already exists in BambooHR',
      employeeData: employeeData,
      skipCreation: true
    }
  }];
} else {
  console.log('Employee does not exist, proceeding with creation');
  return [{
    json: {
      employeeExists: false,
      employeeData: employeeData,
      skipCreation: false
    }
  }];
}

const response = $input.all()[0];
const employeeData = $('Prepare Employee Data').first().json;
const companyDomain = $('Set Company Domain').first().json.companyDomain;

// Extract employee ID from Location header if available
let employeeId = null;
if (response.headers && response.headers.location) {
  const locationMatch = response.headers.location.match(/\/employees\/(\d+)/);
  if (locationMatch) {
    employeeId = parseInt(locationMatch[1]);
  }
}

return [{
  json: {
    success: true,
    message: "Employee created successfully with offer data",
    employee: {
      id: employeeId,
      name: `${employeeData.originalData.firstName} ${employeeData.originalData.lastName}`,
      email: employeeData.originalData.email,
      department: employeeData.originalData.department,
      division: employeeData.originalData.division,
      jobTitle: employeeData.originalData.jobTitle,
      hireDate: employeeData.originalData.hireDate,
      salary: employeeData.originalData.salary
    },
    offerData: employeeData.offerData,
    bambooHrUrl: employeeId ? 
      `https://${companyDomain}.bamboohr.com/employees/${employeeId}` : null,    createdAt: new Date().toISOString(),
    alreadyExisted: false
  }
}];

// Handle case when employee already exists
const checkResult = $('Process Employee Check').first().json;
const existingEmployee = checkResult.existingEmployee;
const employeeData = checkResult.employeeData;
const companyDomain = $('Set Company Domain').first().json.companyDomain;

// Use ONLY the fields that actually exist in the payload
return [{
  json: {
    success: true,
    message: "Employee already exists in BambooHR",
    employee: {
      id: existingEmployee.id,
      name: `${existingEmployee.firstName} ${existingEmployee.lastName}`,
      email: existingEmployee.email,
      department: existingEmployee.jobInformationDepartment,
      division: existingEmployee.jobInformationDivision,
      jobTitle: existingEmployee.jobInformationJobTitle
      // Note: hireDate, salary, payPer are NOT in your payload
    },
    offerData: {
      offerId: null,
      offerStatus: 'existing_employee',
      salary: null, // Not in your payload
      hireDate: null // Not in your payload
    },
    bambooHrUrl: existingEmployee.id ? 
      `https://${companyDomain}.bamboohr.com/employees/${existingEmployee.id}` : null,
    createdAt: new Date().toISOString(),
    alreadyExisted: true,
    existingEmployeeData: existingEmployee // Include full existing data for reference
  }
}];

